mapper.select:
select sum(d.energy_data_increase_value) as energyValue, d.energy_data_${dateCode} as timeValue

    ,equip_classification as typeValue

from t_energy_data d INNER JOIN t_energy_equip e  on d.node_id = e.node_id
where d.energy_data_time >= #{startTime} and d.energy_data_time <= #{endTime}

    and  e.equip_type = #{energyType}


    and  e.equip_classification = #{queryValue}

group by energy_data_${dateCode}

    ,e.equip_classification

select sum(d.energy_data_increase_value) as energyValue, d.energy_data_date as timeValue

    ,equip_area as typeValue

from t_energy_data d INNER JOIN t_energy_equip e  on d.node_id = e.node_id
where d.energy_data_time >= #{startTime} and d.energy_data_time <= #{endTime}

    and  e.equip_type = #{energyType}


    and  e.equip_area = #{queryValue}

group by energy_data_${dateCode}

    ,e.equip_area
