mapper.select:
select sum(d.energy_data_increase_value) as energyValue, d.energy_data_${dateCode} as timeValue

    ,e.equip_classification as typeValue

from t_energy_data d INNER JOIN t_energy_equip e  on d.node_id = e.node_id
where d.energy_data_time >= #{startTime} and d.energy_data_time <= #{endTime}

    and  e.equip_type = #{energyType}


    and  e.equip_classification = #{queryValue}

group by energy_data_${dateCode}

    ,e.equip_classification

select sum(d.energy_data_increase_value) as energyValue, d.energy_data_${dateCode} as timeValue

    ,e.equip_area as typeValue

from t_energy_data d INNER JOIN t_energy_equip e  on d.node_id = e.node_id
where d.energy_data_time >= #{startTime} and d.energy_data_time <= #{endTime}

    and  e.equip_type = #{energyType}


    and  e.equip_area = #{queryValue}

group by energy_data_${dateCode}

    ,e.equip_area

SELECT tcs.setting_value AS x, SUM(ted.energy_data_increase_value) AS y FROM t_energy_data ted LEFT JOIN t_energy_equip tee ON ted.node_id=tee.node_id AND tee.is_active=1 LEFT JOIN t_config_setting tcs ON tee.equip_area=tcs.setting_code AND tcs.setting_type=#{energyConfigSetting} AND tcs.is_active=1 WHERE ted.is_active=1 AND ted.energy_data_source=1
AND energy_data_type=#{equipType}
AND energy_data_time BETWEEN #{startTime} AND #{endTime}
GROUP BY tcs.setting_value ORDER BY SUM(ted.energy_data_increase_value) DESC LIMIT #{lmt}

SELECT SUM(energy_data_increase_value) FROM t_energy_data WHERE is_active=1 AND energy_data_source=1
AND energy_data_type=#{equipType}
AND energy_data_time BETWEEN #{startTime} AND #{endTime}
