mapper.select:
select sum(d.energy_data_increase_value) as energyValue, d.energy_data_${dateCode} as timeValue,e.equip_classification as typeValue
from t_energy_data d INNER JOIN t_energy_equip e  on d.node_id = e.node_id
WHERE d.is_active=1 and e.id != 245638168865542144

    and d.energy_data_time >= #{startTime} and d.energy_data_time <= #{endTime}


    and  e.equip_type = #{energyType}


    and  e.equip_classification = #{queryValue}

group by energy_data_${dateCode}

    ,e.equip_classification

select sum(d.energy_data_increase_value) as energyValue, d.energy_data_${dateCode} as timeValue ,e.equip_area as typeValue
from t_energy_data d INNER JOIN t_energy_equip e  on d.node_id = e.node_id
WHERE d.is_active=1 and e.id != 245638168865542144

    and d.energy_data_time >= #{startTime} and d.energy_data_time <= #{endTime}



    and  e.equip_type = #{energyType}


    and  e.equip_area = #{queryValue}

group by energy_data_${dateCode}

    ,e.equip_area

SELECT tcs.setting_value AS x, SUM(IFNULL(ted.energy_data_increase_value,0)) AS y FROM t_energy_equip tee LEFT JOIN t_energy_data ted ON ted.node_id=tee.node_id AND ted.is_active=1
AND ted.energy_data_type=#{equipType} AND ted.node_id!=245638168865542144
AND ted.energy_data_type=#{equipType}
AND ted.energy_data_time >= #{startTime} AND energy_data_time < #{endTime}
AND ted.energy_data_source=1 LEFT JOIN t_config_setting tcs
ON tee.equip_area=tcs.setting_code
ON tee.equip_classification=tcs.setting_code
AND tcs.setting_type=#{energyConfigSetting} AND tcs.is_active=1 WHERE tee.is_active=1 GROUP BY tcs.setting_value ORDER BY SUM(IFNULL(ted.energy_data_increase_value,0)) DESC LIMIT #{lmt}

SELECT
SUM(energy_data_increase_value)
FROM t_energy_data
WHERE is_active=1
AND energy_data_source=1


        AND energy_data_type=#{equipType}
        AND node_id=245638168865542144


        AND energy_data_type=#{equipType}



    AND energy_data_time >= #{startTime} AND energy_data_time < #{endTime}

SELECT
SUM(energy_data_increase_value)
FROM t_energy_data
WHERE is_active=1
AND energy_data_source=1


        AND energy_data_type=#{equipType}
        AND node_id=245638168865542144


        AND energy_data_type=#{equipType}



    AND energy_data_year=#{year}

SELECT t.energy_data_year AS year,COUNT(1) AS days FROM ( SELECT energy_data_year,energy_data_date FROM t_energy_data WHERE is_active=1 AND energy_data_source=1 GROUP BY energy_data_year,energy_data_date ) t WHERE 1=1
AND t.energy_data_year=#{year}
GROUP BY t.energy_data_year

SELECT id FROM t_energy_data
WHERE is_active=1
AND energy_data_source=1
AND energy_data_type=#{energyDataType}
AND energy_data_time=#{energyDataTime}
AND node_id=#{nodeId}
