mapper.select:
SELECT id FROM t_energy_equip
WHERE equip_no=#{equipNo}
OR equip_name=#{equipName}

SELECT tee.id, tee.node_id, tee.equip_name, tee.equip_no, tee.equip_type, tcs_1.setting_value AS equipTypeStr, tee.equip_floor, tcs_2.setting_value AS equipFloorStr, tee.equip_area, tcs_3.setting_value AS equipAreaStr, tee.equip_classification, tcs_4.setting_value AS equipClassificationStr, tee.comment, teev.verify_time, teev.verify_value FROM t_energy_equip tee LEFT JOIN t_energy_equip_verify teev ON tee.id=teev.equip_id AND teev.is_active=1 AND teev.enable_flag=1 LEFT JOIN t_config_setting tcs_1 ON tee.equip_type=tcs_1.setting_code AND tcs_1.setting_type='equip_type' AND tcs_1.is_active=1 LEFT JOIN t_config_setting tcs_2 ON tee.equip_floor=tcs_2.setting_code AND tcs_2.setting_type='equip_floor' AND tcs_2.is_active=1 LEFT JOIN t_config_setting tcs_3 ON tee.equip_area=tcs_3.setting_code AND tcs_3.setting_type='equip_area' AND tcs_3.is_active=1 LEFT JOIN t_config_setting tcs_4 ON tee.equip_classification=tcs_4.setting_code AND tcs_4.setting_type='equip_classification' AND tcs_4.is_active=1 WHERE tee.is_active=1
AND LOCATE(#{equipName},tee.equip_name)
AND tcs_1.setting_code=#{equipType}
AND tcs_2.setting_code=#{equipFloor}
AND tcs_3.setting_code=#{equipArea}
AND tcs_4.setting_code=#{equipClassification}

SELECT tee.id, tee.equip_name, tee.equip_no, tee.equip_type, tcs_1.setting_value AS equipTypeStr, tee.equip_floor, tcs_2.setting_value AS equipFloorStr, tee.equip_area, tcs_3.setting_value AS equipAreaStr, tee.equip_classification, tcs_4.setting_value AS equipClassificationStr, tee.comment, ted.energy_data_time, ted.energy_data_value FROM t_energy_equip tee LEFT JOIN t_energy_data ted ON tee.node_id=ted.node_id AND ted.is_active=1 AND ted.energy_data_source=1 LEFT JOIN t_config_setting tcs_1 ON tee.equip_type=tcs_1.setting_code AND tcs_1.setting_type='equip_type' AND tcs_1.is_active=1 LEFT JOIN t_config_setting tcs_2 ON tee.equip_floor=tcs_2.setting_code AND tcs_2.setting_type='equip_floor' AND tcs_2.is_active=1 LEFT JOIN t_config_setting tcs_3 ON tee.equip_area=tcs_3.setting_code AND tcs_3.setting_type='equip_area' AND tcs_3.is_active=1 LEFT JOIN t_config_setting tcs_4 ON tee.equip_classification=tcs_4.setting_code AND tcs_4.setting_type='equip_classification' AND tcs_4.is_active=1 WHERE tee.is_active=1
AND LOCATE(#{equipName},tee.equip_name)
AND tcs_1.setting_code=#{equipType}
AND tcs_2.setting_code=#{equipFloor}
AND tcs_3.setting_code=#{equipArea}
AND tcs_4.setting_code=#{equipClassification}
AND ted.energy_data_time=#{energyDataTime}
ORDER BY ted.energy_data_time DESC
